import{_ as t,c as d,o as e,a4 as o}from"./chunks/framework.Xpy1Nj1z.js";const p=JSON.parse('{"title":"HTML5语义化标签","description":"","frontmatter":{},"headers":[],"relativePath":"HTML/HTML5语义化标签.md","filePath":"HTML/HTML5语义化标签.md","lastUpdated":1717483364000}'),r={name:"HTML/HTML5语义化标签.md"},a=o('<h1 id="html5语义化标签" tabindex="-1">HTML5语义化标签 <a class="header-anchor" href="#html5语义化标签" aria-label="Permalink to &quot;HTML5语义化标签&quot;">​</a></h1><h2 id="什么是语义元素" tabindex="-1">什么是语义元素？ <a class="header-anchor" href="#什么是语义元素" aria-label="Permalink to &quot;什么是语义元素？&quot;">​</a></h2><p>语义元素清楚地向浏览器和开发者描述其意义。</p><p>非语义元素的例子：<code>&lt;div&gt;</code> 和 <code>&lt;span&gt;</code> 无法提供关于其内容的信息。</p><p>语义元素的例子：<code>&lt;form&gt;</code>、<code>&lt;table&gt;</code> 、 <code>&lt;img&gt;</code> 能清晰地定义其内容。</p><h2 id="为什么要语义化" tabindex="-1">为什么要语义化？ <a class="header-anchor" href="#为什么要语义化" aria-label="Permalink to &quot;为什么要语义化？&quot;">​</a></h2><ul><li><strong>代码结构</strong> - 使页面没有css的情况下，也能够呈现出很好的内容结构。</li><li><strong>有利于SEO</strong> - 爬虫依赖标签来确定关键字的权重，因此可以和搜索引擎建立良好的沟通，帮助爬虫抓取更多的有效信息。</li><li><strong>提升用户体验</strong> - 例如title、alt可以用于解释名称或者解释图片信息，以及label标签的灵活运用。</li><li><strong>便于团队开发和维护</strong> - 语义化使得代码更具有可读性，让其他开发人员更加理解你的html结构，减少差异化。</li><li><strong>方便其他设备解析</strong> - 如屏幕阅读器、盲人阅读器、移动设备等，以有意义的方式来渲染网页。</li></ul><h2 id="html5-中的语义元素" tabindex="-1">HTML5 中的语义元素 <a class="header-anchor" href="#html5-中的语义元素" aria-label="Permalink to &quot;HTML5 中的语义元素&quot;">​</a></h2><p>下面列出了 HTML5 新语义元素。</p><table><thead><tr><th>标签</th><th>描述</th></tr></thead><tbody><tr><td><code>&lt;article&gt;</code></td><td>定义文章。</td></tr><tr><td><code>&lt;aside&gt;</code></td><td>定义页面内容以外的内容。</td></tr><tr><td><code>&lt;details&gt;</code></td><td>定义用户能够查看或隐藏的额外细节。</td></tr><tr><td><code>&lt;figcaption&gt;</code></td><td>定义 <code>&lt;figure&gt;</code> 元素的标题。</td></tr><tr><td><code>&lt;figure&gt;</code></td><td>规定自包含内容，比如图示、图表、照片、代码清单等。</td></tr><tr><td><code>&lt;footer&gt;</code></td><td>定义文档或节的页脚。</td></tr><tr><td><code>&lt;header&gt;</code></td><td>规定文档或节的页眉。</td></tr><tr><td><code>&lt;main&gt;</code></td><td>规定文档的主内容。</td></tr><tr><td><code>&lt;mark&gt;</code></td><td>定义重要的或强调的文本。</td></tr><tr><td><code>&lt;nav&gt;</code></td><td>定义导航链接。</td></tr><tr><td><code>&lt;section&gt;</code></td><td>定义文档中的节。</td></tr><tr><td><code>&lt;summary&gt;</code></td><td>定义 <code>&lt;details&gt;</code> 元素的可见标题。</td></tr><tr><td><code>&lt;time&gt;</code></td><td>定义日期/时间。</td></tr></tbody></table>',10),l=[a];function c(i,s,n,h,g,_){return e(),d("div",null,l)}const T=t(r,[["render",c]]);export{p as __pageData,T as default};
